local helpers = require 'helpers'

local digits = ""

local function answer ()
  local greatest_product = 0
  local greatest_digits
  for i = 1, #digits - 5 do
    local d1 = tonumber(digits:sub(i, i))
    local d2 = tonumber(digits:sub(i + 1, i + 1))
    local d3 = tonumber(digits:sub(i + 2, i + 2))
    local d4 = tonumber(digits:sub(i + 3, i + 3))
    local d5 = tonumber(digits:sub(i + 4, i + 4))
    local res = d1 * d2 * d3 * d4 * d5
    if res > greatest_product then
      greatest_product = res
      greatest_digits = digits:sub(i, i + 4)
    end
  end
  return greatest_product, greatest_digits
end

helpers.elapsed_time(function ()
  local greatest_product, greatest_digits = answer()
  print(greatest_product, greatest_digits)
end)
